import React, { useRef, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import "./MorphCardTabs.css"; // 這是專屬樣式檔

export default function MorphCardTabs({
  data,
  active,
  setActive,
  containerRef,
}) {
  // 點擊空白處關閉展開卡片
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (!e.target.closest(".morph-card")) {
        setActive(null);
      }
    };
    window.addEventListener("click", handleClickOutside);
    return () => window.removeEventListener("click", handleClickOutside);
  }, []);

  const cardRefs = useRef({});

  useEffect(() => {
    const container = containerRef?.current;
    const activeEl = cardRefs.current[active];
    if (!container || !activeEl) return;

    if (window.innerWidth < 768) {
      // 卡片在容器內的位置
      const cardRect = activeEl.getBoundingClientRect();
      const containerRect = container.getBoundingClientRect();

      // 水平置中
      const scrollLeft =
        container.scrollLeft +
        (cardRect.left - containerRect.left) -
        (containerRect.width / 2 - cardRect.width / 2);

      // 垂直置中（如果需要）
      const scrollTop =
        container.scrollTop +
        (cardRect.top - containerRect.top) -
        (containerRect.height / 2 - cardRect.height / 2);

      container.scrollTo({
        left: scrollLeft,
        top: scrollTop,
        behavior: "smooth",
      });
    }
  }, [active]);
  return (
    <div className="morph-container">
      {data.map((tab) => (
        <motion.div
          key={tab.id}
          ref={(el) => (cardRefs.current[tab.id] = el)}
          layoutId={`card-${tab.id}`}
          layout
          transition={{ layout: { duration: 0.5, ease: "easeInOut" } }}
          className={`morph-card ${active === tab.id ? "expanded" : ""}`}
          onClick={(e) => {
            e.stopPropagation();
            setActive(active === tab.id ? null : tab.id);
          }}
        >
          <motion.h3>{tab.title}</motion.h3>

          <motion.div
            className="morph-content-wrapper"
            initial={false}
            animate={{
              opacity: active === tab.id ? 1 : 0,
              width: active === tab.id ? "auto" : 0,
              height: active === tab.id ? "auto" : 0,
              marginTop: active === tab.id ? "0.5rem" : 0,
            }}
            transition={{ duration: 0.4, ease: "easeInOut" }}
          >
            <div className="morph-content">
              <p>{tab.content}</p>
            </div>
          </motion.div>
        </motion.div>
      ))}
    </div>
  );
}
