import { useState, useEffect, forwardRef } from "react";
import selfie from "../images/selfie.png";
import { ReactComponent as Wave1_m } from "../images/mobile/wave1.svg";
import { ReactComponent as Wave2_m } from "../images/mobile/wave2.svg";
import { ReactComponent as Wave1_d } from "../images/desktop/wave1.svg";
import { ReactComponent as Wave2_d } from "../images/desktop/wave2.svg";

const images = [
  "leaf1",
  "flower",
  "leaf1_",
  "leaf2",
  "leaf1_2",
  "flower_",
  "leaf2_",
];
const mobileImages = {};
const desktopImages = {};

images.forEach((name) => {
  mobileImages[name] = require(`../images/mobile/${name}.png`);
  desktopImages[name] = require(`../images/desktop/${name}.png`);
});

const Home = forwardRef(({ id }, ref) => {
  const [isDesktop, setIsDesktop] = useState(window.innerWidth >= 768);

  useEffect(() => {
    const isDesktopDetect = () => {
      setIsDesktop(window.innerWidth >= 768);
    };
    window.addEventListener("resize", isDesktopDetect);
    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  return (
    <div id={id} ref={ref}>
      <div className="homeContainer">
        <div className="titleContainer">
          <div className="title">HI!</div>
          <div className="subtitle">
            我是<span>UI</span>設計師
          </div>
        </div>
      </div>
      <div className="homeBg">
        {images.map((name) => (
          <picture key={name}>
            <source srcSet={desktopImages[name]} media="(min-width:768px)" />
            <img className={name} src={mobileImages[name]} alt="" />
          </picture>
        ))}

        <img className="selfie" src={selfie} alt="" />
        {isDesktop ? (
          <>
            {/* <Wave1_d className="wave1" />
            <Wave1_d className="wave1_" /> */}
            <Wave2_d className="wave2" />
            <img
              className="flower_2"
              src={require("../images/desktop/flower_2.png")}
              alt=""
            />
            <img
              className="flower_2"
              src={require("../images/desktop/leaf1_3.png")}
              alt=""
            />
          </>
        ) : (
          <>
            {/* <Wave1_m className="wave1" />
            <Wave1_m className="wave1_" /> */}
            <Wave2_m className="wave2" />
          </>
        )}
      </div>
    </div>
  );
});

export default Home;
