import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import "./MorphCardTabs.css"; // 這是專屬樣式檔

export default function MorphCardTabs({ data }) {
  const [active, setActive] = useState(null);

  // 點擊空白處關閉展開卡片
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (!e.target.closest(".morph-card")) {
        setActive(null);
      }
    };
    window.addEventListener("click", handleClickOutside);
    return () => window.removeEventListener("click", handleClickOutside);
  }, []);

  return (
    <div className="morph-container">
      {data.map((tab) => (
        <motion.div
          key={tab.id}
          layout
          transition={{ layout: { duration: 0.5, ease: "easeInOut" } }}
          className={`morph-card ${active === tab.id ? "expanded" : ""}`}
          onClick={(e) => {
            e.stopPropagation();
            setActive(active === tab.id ? null : tab.id);
          }}
        >
          <motion.h3>{tab.title}</motion.h3>

          <motion.div
            key="content"
            className="morph-content"
            initial={false}
            animate={{
              opacity: active === tab.id ? 1 : 0,
              height: active === tab.id ? "auto" : 0,
              width: active === tab.id ? "auto" : 0,
              // marginTop: active === tab.id ? "0.5rem" : 0,

            }}
            transition={{ duration: 0.6 }}
          >
            {/* {tab.image && <img src={tab.image} alt={tab.title} />} */}
            <div className="inner">
              <p>{tab.content}</p>
            </div>
          </motion.div>
        </motion.div>
      ))}
    </div>
  );
}
