import React, { useRef, useEffect, useState } from "react";
import { motion, AnimatePresence, delay } from "framer-motion";
import "./UserJourneyMap.css";

export default function UserJourneyMap({ stages, rows }) {
  return (
    <div className="journey-map">
      {/* Ë°®È†≠ */}
      <div className="header empty"></div>
      {stages.map((stage) => (
        <div key={stage} className="header">
          {stage}
        </div>
      ))}

      {/* ÂêÑË°å */}
      {rows.map((row) => (
        <React.Fragment key={row.label}>
          <div className="row-label">{row.label}</div>
          {row.type === "emotion" ? (
            // üé¢ ÊÉÖÁ∑íÊõ≤Á∑öË°å
            <AnimatePresence mode="wait">
              <motion.div
                key={JSON.stringify(row.data)}
                className="emotion-line"
                initial={{ opacity: 0, y: 7 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: 7 }}
                transition={{ duration: 0.2 }}
                style={{ gridColumn: "span 4" }}
              >
                <EmotionLine values={row.data} />
              </motion.div>
            </AnimatePresence>
          ) : (
            // ÂÖ∂‰ªñ‰∏ÄËà¨Ë°å
            row.data.map((cell, i) => (
              <div key={i} className="cell">
                <FadingCell content={cell} sequence={i} />
              </div>
            ))
          )}
        </React.Fragment>
      ))}
    </div>
  );
}

/* Â≠êÂÖÉ‰ª∂ÔºöÂ∏∂Ê∑°ÂÖ•Ê∑°Âá∫ÊïàÊûúÁöÑÊñáÂ≠ó */
function FadingCell({ content, sequence }) {
  return (
    <AnimatePresence mode="wait">
      <motion.div
        key={content} // key ËÆäÂåñÊôÇËß∏ÁôºÂãïÁï´
        initial={{ opacity: 0, y: 7 }}
        animate={{ opacity: 1, y: 0 }}
        exit={{ opacity: 0, y: 7 }}
        transition={{ duration: 0.2, delay: 0.1 * sequence }}
        dangerouslySetInnerHTML={{ __html: content }}
      />
    </AnimatePresence>
  );
}

/* ‚úÖ Â≠êÂÖÉ‰ª∂ÔºöÁπ™Ë£ΩÊÉÖÁ∑íÊäòÁ∑öÂúñ */
function EmotionLine({ values = [], padding = 20 }) {
  const containerRef = useRef(null);
  const pathRef = useRef(null);
  const [size, setSize] = useState({ width: 500, height: 200 });
  const [pathLength, setPathLength] = useState(0);
  const [dashOffset, setDashOffset] = useState(0);
  const [progress, setProgress] = useState(0); // 0~1 Áî®‰æÜÊéßÂà∂ÂúìÈªû

  // Áõ£Ê∏¨ÂÆπÂô®Â§ßÂ∞è
  useEffect(() => {
    const updateSize = () => {
      if (containerRef.current) {
        setSize({
          width: containerRef.current.clientWidth || 500,
          height: containerRef.current.clientHeight || 200,
        });
      }
    };
    updateSize();
    window.addEventListener("resize", updateSize);
    return () => window.removeEventListener("resize", updateSize);
  }, []);

  const { width, height } = size;
  if (!values.length) return null;

  const step = (width - padding) / Math.max(values.length - 1, 1);

  // ÁîüÊàê path d
  const pathD = values
    .map(
      (v, i) =>
        `${i === 0 ? "M" : "L"} ${i * step + padding / 2} ${
          height - (v / 100) * height
        }`
    )
    .join(" ");

  // ÂèñÂæó path Èï∑Â∫¶
  useEffect(() => {
    if (pathRef.current) {
      const len = pathRef.current.getTotalLength();
      setPathLength(len);
      setDashOffset(len);
    }
  }, [pathD, width, height]);

  // Á∑öÊ¢ùÂãïÁï´
  useEffect(() => {
    if (pathLength === 0) return;
    let start = null;
    const duration = 2000; // 2 Áßí

    const animate = (timestamp) => {
      if (!start) start = timestamp;
      const elapsed = timestamp - start;
      const t = Math.min(elapsed / duration, 1);
      setDashOffset(pathLength * (1 - t));
      setProgress(t);
      if (t < 1) {
        requestAnimationFrame(animate);
      }
    };
    requestAnimationFrame(animate);
  }, [pathLength]);

  return (
    <div
      ref={containerRef}
      style={{ width: "100%", height: "100%", overflow: "visible" }}
    >
      <svg
        viewBox={`0 0 ${width} ${height}`}
        preserveAspectRatio="xMidYMid meet"
        style={{ width: "100%", height: "100%" }}
      >
        {/* ÁÅ∞Â∫ïÁ∑ö */}
        <path d={pathD} fill="none" stroke="rgba(255,255,255,0.2)" strokeWidth="2" />

        {/* ËóçÁ∑öÂãïÁï´ */}
        {pathLength > 0 && (
          <path
            ref={pathRef}
            d={pathD}
            fill="none"
            stroke="#00bfff"
            strokeWidth="2"
            strokeLinecap="round"
            strokeDasharray={pathLength}
            strokeDashoffset={dashOffset}
          />
        )}

        {/* ÂúìÈªûÂãïÁï´ */}
        {values.map((v, i) => {
          const pointProgress = i / (values.length - 1);
          return (
            <circle
              key={i}
              cx={i * step + padding / 2}
              cy={height - (v / 100) * height}
              r="3"
              fill="#00bfff"
              style={{
                opacity: progress >= pointProgress ? 1 : 0,
                transition: "opacity 0.1s linear",
              }}
            />
          );
        })}
      </svg>
    </div>
  );
}
