import React from "react";
import useReveal from "./useReveal";
import "./reveal.css";

function Reveal({ children }) {
  const [ref, isVisible] = useReveal();

  return (
    <div ref={ref} className={`reveal ${isVisible ? "active" : ""}`}>
      {children}
    </div>
  );
}

export default Reveal;
