import React, { useRef, useEffect, useState } from "react";
import { motion, AnimatePresence, delay } from "framer-motion";
import "./UserJourneyMap.css";

export default function UserJourneyMap({ stages, rows }) {
  return (
    <div className="journey-map">
      {/* Ë°®È†≠ */}
      <div className="header empty"></div>
      {stages.map((stage) => (
        <div key={stage} className="header">
          {stage}
        </div>
      ))}

      {/* ÂêÑË°å */}
      {rows.map((row) => (
        <React.Fragment key={row.label}>
          <div className="row-label">{row.label}</div>
          {row.type === "emotion" ? (
            // üé¢ ÊÉÖÁ∑íÊõ≤Á∑öË°å
            <AnimatePresence mode="wait">
              <motion.div
                key={JSON.stringify(row.data)}
                className="emotion-line"
                initial={{ opacity: 0, y: 7 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: 7 }}
                transition={{ duration: 0.2 }}
                style={{ gridColumn: "span 4" }}
              >
                <EmotionLine values={row.data} />
              </motion.div>
            </AnimatePresence>
          ) : (
            // ÂÖ∂‰ªñ‰∏ÄËà¨Ë°å
            row.data.map((cell, i) => (
              <div key={i} className="cell">
                <FadingCell content={cell} sequence={i} />
              </div>
            ))
          )}
        </React.Fragment>
      ))}
    </div>
  );
}

/* Â≠êÂÖÉ‰ª∂ÔºöÂ∏∂Ê∑°ÂÖ•Ê∑°Âá∫ÊïàÊûúÁöÑÊñáÂ≠ó */
function FadingCell({ content, sequence }) {
  return (
    <AnimatePresence mode="wait">
      <motion.div
        key={content} // key ËÆäÂåñÊôÇËß∏ÁôºÂãïÁï´
        initial={{ opacity: 0, y: 7 }}
        animate={{ opacity: 1, y: 0 }}
        exit={{ opacity: 0, y: 7 }}
        transition={{ duration: 0.2, delay: 0.1 * sequence }}
        dangerouslySetInnerHTML={{ __html: content }}
      />
    </AnimatePresence>
  );
}

/* ‚úÖ Â≠êÂÖÉ‰ª∂ÔºöÁπ™Ë£ΩÊÉÖÁ∑íÊäòÁ∑öÂúñ */

function EmotionLine({ values, padding = 20 }) {
  const containerRef = useRef(null);
  const pathRef = useRef(null);
  const [size, setSize] = useState({ width: 500, height: 200 });
  const [pathLength, setPathLength] = useState(null); // ÊîπÁÇ∫ null Ëµ∑Âßã
  const [ready, setReady] = useState(false); // ÊéßÂà∂ÂãïÁï´ÂïüÂãïÊôÇÊ©ü

  // Áõ£Ê∏¨ÂÆπÂô®Â∞∫ÂØ∏ËÆäÂåñ
  useEffect(() => {
    const updateSize = () => {
      if (containerRef.current) {
        const { clientWidth, clientHeight } = containerRef.current;
        setSize({
          width: clientWidth || 500,
          height: clientHeight || 200,
        });
      }
    };
    updateSize();
    window.addEventListener("resize", updateSize);
    return () => window.removeEventListener("resize", updateSize);
  }, []);

  const { width, height } = size;
  if (!values || values.length === 0) return null;

  const step = (width - padding) / Math.max(values.length - 1, 1);

  const points = values
    .map((v, i) => {
      const x = i * step + padding / 2;
      const y = height - (v / 100) * height;
      return `${x},${y}`;
    })
    .join(" ");

  // ÈáèÊ∏¨ path Èï∑Â∫¶
  useEffect(() => {
    if (pathRef.current) {
      const len = pathRef.current.getTotalLength();
      if (!isNaN(len)) {
        setPathLength(len);
        // ÁÇ∫‰∫ÜËÆì motion Âú®ÊúâÈï∑Â∫¶ÂæåÊâçÂïüÂãïÂãïÁï´
        setTimeout(() => setReady(true), 0);
      }
    }
  }, [points]);

  if (pathLength === null) {
    return <div ref={containerRef} style={{ width: "100%", height: "100%" }} />;
  }

  return (
    <div
      ref={containerRef}
      style={{ width: "100%", height: "100%", overflow: "visible" }}
    >
      <svg
        viewBox={`0 0 ${width} ${height}`}
        preserveAspectRatio="xMidYMid meet"
        style={{ width: "100%", height: "100%" }}
      >
        {/* ÊäòÁ∑öÂãïÁï´ */}
        <motion.polyline
          ref={pathRef}
          fill="none"
          stroke="#00bfff"
          strokeWidth="2"
          strokeLinecap="round"
          points={points}
          // ‚úÖ Âè™ÊúâÂú® ready ÂæåÊâçË®≠ÂÆöÂãïÁï´Ëµ∑Èªû
          initial={
            ready
              ? {
                  strokeDasharray: pathLength,
                  strokeDashoffset: pathLength,
                }
              : false
          }
          animate={ready ? { strokeDashoffset: 0 } : {}}
          transition={{
            duration: 1.8,
            ease: "easeInOut",
          }}
        />

        {/* ÁØÄÈªûÊ∑°ÂÖ• */}
        {ready &&
          values.map((v, i) => (
            <motion.circle
              key={i}
              cx={i * step + padding / 2}
              cy={height - (v / 100) * height}
              r="3"
              fill="#00bfff"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{
                delay: 0.3 + i * 0.08,
                duration: 0.2,
              }}
            />
          ))}
      </svg>
    </div>
  );
}
