import { useState, useEffect, forwardRef } from "react";
import selfie from "../images/selfie.png";
import { ReactComponent as Wave1_m } from "../images/mobile/wave1.svg";
import { ReactComponent as Wave2_m } from "../images/mobile/wave2.svg";
import { ReactComponent as Wave1_d } from "../images/desktop/wave1.svg";
import { ReactComponent as Wave2_d } from "../images/desktop/wave2.svg";

const images = [
  "leaf1",
  "flower",
  "leaf1_",
  "leaf2",
  "leaf1_2",
  "flower_",
  "leaf2_",
];
const mobileImages = {};
const desktopImages = {};

images.forEach((name) => {
  mobileImages[name] = require(`../images/mobile/${name}.png`);
  desktopImages[name] = require(`../images/desktop/${name}.png`);
});

const Home = forwardRef(({ id }, ref) => {
  const [isDesktop, setIsDesktop] = useState(window.innerWidth >= 768);

  useEffect(() => {
    const isDesktopDetect = () => {
      setIsDesktop(window.innerWidth >= 768);
    };
    window.addEventListener("resize", isDesktopDetect);
    return () => {
      window.removeEventListener("resize", isDesktopDetect);
    };
  }, []);

  return (
    <div id={id} ref={ref}>

    </div>
  );
});

export default Home;
