import React, { useRef, useEffect } from "react";
import { motion } from "framer-motion";
import "./MorphCardTabs.css";

export default function MorphCardTabs({ data, active, setActive, containerRef }) {
  const cardRefs = useRef({});

  useEffect(() => {
    const handleClickOutside = (e) => {
      if (!e.target.closest(".morph-card")) setActive(null);
    };
    window.addEventListener("click", handleClickOutside);
    return () => window.removeEventListener("click", handleClickOutside);
  }, [setActive]);

  useEffect(() => {
    const container = containerRef?.current;
    const activeEl = cardRefs.current[active];
    if (!container || !activeEl) return;

    const containerWidth = container.offsetWidth;
    const cardLeft = activeEl.offsetLeft;
    const cardWidth = activeEl.offsetWidth;
    const scrollLeft = cardLeft - containerWidth / 2 + cardWidth / 2;

    console.log("container:", container);
    console.log("activeEl:", activeEl);
    console.log("container.scrollLeft before:", container.scrollLeft);
    console.log("scrollTo →", scrollLeft);

    container.scrollTo({
      left: scrollLeft,
      behavior: "smooth",
    });

    // 顯示目前位置（幫助你確認容器真的在動）
    setTimeout(() => {
      console.log("after scrollLeft:", container.scrollLeft);
    }, 600);
  }, [active, containerRef]);

  return (
    <div className="morph-container" style={{ border: "1px dashed red" }}>
      {data.map((tab) => (
        <motion.div
          key={tab.id}
          ref={(el) => (cardRefs.current[tab.id] = el)}
          layoutId={`card-${tab.id}`}
          layout
          transition={{ layout: { duration: 0.5, ease: "easeInOut" } }}
          className={`morph-card ${active === tab.id ? "expanded" : ""}`}
          onClick={(e) => {
            e.stopPropagation();
            setActive(active === tab.id ? null : tab.id);
          }}
          style={{
            border: active === tab.id ? "2px solid orange" : "1px solid #546850",
            minWidth: "fit-content",
          }}
        >
          <motion.h3>{tab.title}</motion.h3>

          <motion.div
            className="morph-content-wrapper"
            initial={false}
            animate={{
              opacity: active === tab.id ? 1 : 0,
              width: active === tab.id ? "auto" : 0,
              height: active === tab.id ? "auto" : 0,
              marginTop: active === tab.id ? "0.5rem" : 0,
            }}
            transition={{ duration: 0.4, ease: "easeInOut" }}
          >
            <div className="morph-content">
              <p>{tab.content}</p>
            </div>
          </motion.div>
        </motion.div>
      ))}
    </div>
  );
}
