import React, { useRef, useEffect, useState, forwardRef } from "react";
import { ReactComponent as MenuIcon } from "./images/MenuIcon.svg";
import classNames from "classnames";

function Header({ sectionRefs }) {
  const [activeSection, setActiveSection] = useState("");

  useEffect(() => {
    //設定觀察條件
    const options = {
      root: null, // 用「視窗」來當觀察範圍
      threshold: 0.5, // 元素至少 50% 出現在視窗中才算「進入」
    };
    // 建立觀察器
    const observer = new IntersectionObserver((entries) => {
      entries.forEach((entry) => {
        // 如果某個被觀察的元素「進入」了觀察範圍（50% 可見）
        if (entry.isIntersecting) {
          //console.log("現在出現的是區塊：", entry.target.id);
          setActiveSection(entry.target.id);
        }
      });
    }, options);
    // 導入該物件的每個值，觀察該值的ref元素（Home 區塊、About 區塊...）
    // 一定要 observe 該 ref.current(在html裡面插ref的該元素) 才能進到 entries
    Object.values(sectionRefs).forEach((ref) => {
      if (ref.current) observer.observe(ref.current);
    });
    // 清理函式：元件卸載時停止觀察
    return () => observer.disconnect();
  }, []);

  function movePage(page) {
    const target = document.getElementById(page);
    target?.scrollIntoView({ behavior: "smooth" });
  }

  const [isOpen, setIsOpen] = useState(false);
  const toggleMenu = () => {
    setIsOpen((prev) => !prev);
    console.log(isOpen);
  };
  return (
    <div id="header">
      <div className="logo">
        Portfolio <br />
        <span>Benjamin.</span>
      </div>
      <div className="menu_mobile">
        <button
          className="MenuButton_Icon"
          onClick={toggleMenu}
          aria-expanded={isOpen}
          aria-controls="menu-list"
        >
          <MenuIcon className="Icon" />
        </button>
      </div>
      <div
        id="menu-list"
        className="menu"
        style={{
          transformOrigin: "top",
          transform: isOpen ? "scaleT(1)" : "scaleY(0)",
          opacity: isOpen ? 1 : 0,
          transition: "all 0.3s ease",
          pointerEvents: isOpen ? "auto" : "none",
        }}
      >
        <div className="menuContainer">
          <button
            className={classNames("MenuButton", {
              active: activeSection === "home",
            })}
            onClick={() => movePage("home")}
          >
            首頁
          </button>
          <button
            className={classNames("MenuButton", {
              active: activeSection === "about",
            })}
            onClick={() => movePage("about")}
          >
            介紹
          </button>
          <button
            className={classNames("MenuButton", {
              active: activeSection === "skill",
            })}
            onClick={() => movePage("skill")}
          >
            技能
          </button>
          <button
            className={classNames("MenuButton", {
              active: activeSection === "experience",
            })}
            onClick={() => movePage("experience")}
          >
            經歷
          </button>

          <button
            className={classNames("MenuButton", {
              active: activeSection === "works",
            })}
            onClick={() => movePage("works")}
          >
            作品
          </button>
          <button
            className={classNames("MenuButton", {
              active: activeSection === "contact",
            })}
            onClick={() => movePage("contact")}
          >
            聯絡
          </button>
        </div>
      </div>
    </div>
  );
}
export default Header;
