import React, { useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import "./MorphCardTabs.css";

export default function MorphCardTabs({ data, active, setActive }) {
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (!e.target.closest(".morph-card")) setActive(null);
    };
    window.addEventListener("click", handleClickOutside);
    return () => window.removeEventListener("click", handleClickOutside);
  }, [setActive]);

  const isMobile = window.matchMedia("(max-width: 768px)").matches;

  return (
    <div
      className="morph-container"
      style={{
        justifyContent: active && isMobile ? "center" : "flex-start",
        transition: "justify-content 0.35s ease",
      }}
    >
      <AnimatePresence mode="popLayout">
        {data.map((tab) => {
          const isActive = active === tab.id;
          const isHidden = isMobile && active && !isActive;
          return (
            <motion.div
              key={tab.id}
              layoutId={`card-${tab.id}`}
              layout
              className={`morph-card ${isActive ? "expanded" : ""}`}
              initial={false}
              animate={{
                opacity: isHidden ? 0 : 1,
                scale: isHidden ? 0.7 : 1,
                position: isHidden ? "absolute" : "relative",
                pointerEvents: isHidden ? "none" : "auto",
              }}
              exit={{
                opacity: 0,
                scale: 0.8,
                position: "absolute",
                pointerEvents: "none",
              }}
              transition={{ duration: 0.35, ease: "easeInOut" }}
              onClick={(e) => {
                e.stopPropagation();
                setActive(isActive ? null : tab.id);
              }}
            >
              <motion.h3 layout="position">{tab.title}</motion.h3>

              <AnimatePresence>
                {isActive && (
                  <motion.div
                    className="morph-content-wrapper"
                    key={tab.id + "_content"}
                    initial={{ opacity: 0, height: 0 }}
                    animate={{ opacity: 1, height: "auto" }}
                    exit={{ opacity: 0, height: 0 }}
                    transition={{ duration: 0.4, ease: "easeInOut" }}
                  >
                    <div className="morph-content">
                      <p>{tab.content}</p>
                    </div>
                  </motion.div>
                )}
              </AnimatePresence>
            </motion.div>
          );
        })}
      </AnimatePresence>
    </div>
  );
}
