import React, { forwardRef, useState, useEffect, useRef } from "react";
import MorphCardTabs from "../components/MorphCardTabs_light";
import { motion, AnimatePresence } from "framer-motion";
import Reveal from "../components/Reveal";

const images = ["cover", "motivation"];
const mobileImages = {};
const desktopImages = {};

images.forEach((name) => {
  mobileImages[name] = `/asset/think_${name}_M.png`;
  desktopImages[name] = `/asset/think_${name}_D.png`;
});

const Motivation = () => (
  <motion.div
    key="motivation"
    className="content_motivation"
    initial={{ opacity: 0, y: 40 }}
    animate={{ opacity: 1, y: 0 }}
    exit={{ opacity: 0, y: -40 }}
    transition={{ duration: 0.3 }}
  >
    <div className="context_container">
      <div className="context">
        ÊØèÁï∂Á©∫Èñë‰∏çÁü•ÈÅìÂéªÂì™Ë£°ÁöÑÊôÇÂÄôÔºåÊâìÈñãÂú∞ÂúñËá™Â∑±Êù±ÊâæË•øÊâæÔºåÊúÄÁµÇÈÇÑÊòØÊ≤íÊúâ‰∏ÄÂÄãÁµêÊûú„ÄÇ
        <br />
        <br />
        Â¶ÇÊûú‰∏ÄÂÄãÊÉ≥Ê≥ïÔºåÁï∂ÊàëÊâìÈñãÂú∞ÂúñÔºåÂ∞±ÊúâË®±Â§öË°åÂÆ∂ÈÉΩÂÖàÂπ´‰Ω†Á∂≤ÁæÖÂ•ΩÁöÑÊôØÈªûÔºåÊúÉ‰∏çÊúÉÂø´ÂæàÂ§öÂë¢Ôºü
        <br />
        <br />
        ÊàñË®±ÈÇÑÂèØ‰ª•ÂâµÂª∫ÂêÑÁ®Æ‰∏ªÈ°åÁöÑÂú∞ÂúñÔºå‰∏¶Áµ±Êï¥‰∏ÄÂÄãÂπ≥Âè∞ÔºåËÆìÂ§ßÂÆ∂‰∫íÁõ∏ÂàÜ‰∫´Êé¢Á¥¢ÔºåÂâµÈÄ†Êõ¥Â§öË®éË´ñÁöÑÂèØËÉΩÊÄß„ÄÇ
      </div>
    </div>
    <div className="framer">
      <picture>
        <source
          srcSet={desktopImages["motivation"]}
          media="(min-width:1025px)"
        />
        <motion.img
          src={mobileImages["motivation"]}
          initial={{ opacity: 0, y: 40 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0, y: -40 }}
          transition={{ duration: 0.3 }}
          style={{
            position: "absolute",
            height: "100%",
            top: "50%",
            left: "50%",
            x: "-50%",
            y: "-50%",
            objectFit: "cover",
            minWidth:"none"
          }}
        />
      </picture>
    </div>
  </motion.div>
);
const Research = () => (
  <motion.div
    key="research"
    initial={{ opacity: 0, y: 40 }}
    animate={{ opacity: 1, y: 0 }}
    exit={{ opacity: 0, y: -40 }}
    transition={{ duration: 0.3 }}
  >
    research
  </motion.div>
);
const PainPoints = () => (
  <motion.div
    key="painPoints"
    initial={{ opacity: 0, y: 40 }}
    animate={{ opacity: 1, y: 0 }}
    exit={{ opacity: 0, y: -40 }}
    transition={{ duration: 0.3 }}
  >
    painPoints
  </motion.div>
);
const Solution = () => (
  <motion.div
    key="solution"
    initial={{ opacity: 0, y: 40 }}
    animate={{ opacity: 1, y: 0 }}
    exit={{ opacity: 0, y: -40 }}
    transition={{ duration: 0.3 }}
  >
    solution
  </motion.div>
);

const tabs = [
  {
    id: "motivation",
    title: "ÂãïÊ©ü",
    component: <Motivation />,
  },
  {
    id: "research",
    title: "Ë≥áÊñôËíêÈõÜ",
    component: <Research />,
  },
  {
    id: "painPoints",
    title: "ÂïèÈ°åÈªû",
    component: <PainPoints />,
  },
  {
    id: "solution",
    title: "Ëß£Ê±∫Èªû",
    component: <Solution />,
  },
];

const Works_1 = forwardRef(({ id }, ref) => {
  const [active, setActive] = useState(null);
  const activeTab = tabs.find((tab) => tab.id === active);

  const [isDesktop, setIsDesktop] = useState(window.innerWidth >= 1025);

  useEffect(() => {
    const isDesktopDetect = () => {
      setIsDesktop(window.innerWidth >= 768);
    };
    window.addEventListener("resize", isDesktopDetect);
    return () => {
      window.removeEventListener("resize", isDesktopDetect);
    };
  }, []);

  return (
    <div id={id} ref={ref}>
      <div className="worksContainer">
        <div className="container">
          <div className="tabContainer_">
            <Reveal direction="left" delay={0}>
              <div className="title">
                <span>ÁôºÊÉ≥ËàáË®≠Ë®à</span>
                <p>Think & Design</p>
              </div>
            </Reveal>
            {isDesktop ? (
              <Reveal direction="left" delay={0.2}>
                <div>
                  <MorphCardTabs
                    data={tabs}
                    active={active}
                    setActive={setActive}
                  />
                </div>
              </Reveal>
            ) : null}
          </div>

          <div className="contentContainer_">
            <Reveal direction="left" delay={0.4}>
              <div>
                {activeTab ? (
                  activeTab.component
                ) : (
                  /* üí§ Ê≤íÈÅ∏ÂèñÊôÇÈ°ØÁ§∫Â∞ÅÈù¢ÁÖß */
                  <picture>
                    <source
                      srcSet={desktopImages["cover"]}
                      media="(min-width:1025px)"
                    />
                    <motion.img
                      className="defaultCover_1"
                      src={mobileImages["cover"]}
                      initial={{ opacity: 0, y: 40 }}
                      animate={{ opacity: 1, y: 0 }}
                      exit={{ opacity: 0, y: -40 }}
                      transition={{ duration: 0.3 }}
                    />
                  </picture>
                )}
              </div>
            </Reveal>
          </div>

          {!isDesktop ? (
            <div className="mobileTabContainer">
              <Reveal direction="left" delay={0.2}>
                <div className="mobileTabContent">
                  <MorphCardTabs
                    data={tabs}
                    active={active}
                    setActive={setActive}
                  />
                </div>
              </Reveal>
            </div>
          ) : null}
        </div>
      </div>
    </div>
  );
});

export default Works_1;
