import React, { forwardRef } from "react";
import selfie from "../images/selfie.png";
import { ReactComponent as Wave1 } from "../images/wave1.svg";
import { ReactComponent as Wave2 } from "../images/wave2.svg";
import leaf1 from "../images/mobile/leaf1.png";
import leaf1_ from "../images/mobile/leaf1_.png";
import leaf1_2 from "../images/mobile/leaf1_2.png";
import flower from "../images/mobile/flower.png";
import leaf2 from "../images/mobile/leaf2.png";
import leaf2_ from "../images/mobile/leaf2_.png";
import flower_ from "../images/mobile/flower_.png";

import d_leaf1 from "../images/desktop/leaf1.png";
import d_leaf1_ from "../images/desktop/leaf1_.png";
import d_leaf1_2 from "../images/desktop/leaf1_2.png";
import d_flower from "../images/desktop/flower.png";
import d_leaf2 from "../images/desktop/leaf2.png";
import d_leaf2_ from "../images/desktop/leaf2_.png";
import d_flower_ from "../images/desktop/flower_.png";

const images = [
  "leaf1",
  "flower",
  "leaf1_",
  "leaf2",
  "leaf1_2",
  "flower_",
  "leaf2_",
];
const mobileImages = {};
const desktopImages = {};

images.forEach((name) => {
  mobileImages[name] = require(`../images/mobile/${name}.png`);
  desktopImages[name] = require(`../images/desktop/${name}.png`);
});

const Home = forwardRef(({ id }, ref) => {
  return (
    <div id={id} ref={ref}>
      <div className="homeContainer">
        <div className="titleContainer">
          <div className="title">HI!</div>
          <div className="subtitle">
            我是<span>UI</span>設計師
          </div>
        </div>
      </div>
      <div className="homeBg">
        <Wave2 className="wave2" />

        {images.map((name) => {
          <picture key={name}>
            <source srcSet={desktopImages[name]} media="(min-width:768px)" />
            <img className={name} src={mobileImages[name]} alt="" />
          </picture>;
        })}
        {/* <img className="leaf1" src={leaf1} alt="" />
        <img className="flower" src={flower} alt="" /> */}
        <img className="selfie" src={selfie} alt="" />
        <Wave1 className="wave1" />
        <Wave1 className="wave1_" />
        {/* <img className="leaf1_" src={leaf1_} alt="" />
        <img className="leaf2" src={leaf2} alt="" />
        <img className="leaf1_2 " src={leaf1_2} alt="" />
        <img className="flower_" src={flower_} alt="" />
        <img className="leaf2_" src={leaf2_} alt="" /> */}
      </div>
    </div>
  );
});

export default Home;
