import React, { useState } from "react";
import { ReactComponent as Arrow } from "../images/arrow.svg";
import "./MorphCardTabs.css";

export default function MorphCardTabs({ data }) {
  const [active, setActive] = useState(null);

  return (
    <div className="morph-container">
      {data.map((tab) => {
        const isActive = active === tab.id;
        return (
          <div
            key={tab.id}
            className={`morph-card ${isActive ? "expanded" : ""}`}
            onClick={() => setActive(isActive ? null : tab.id)}
          >
            <h3>
              {tab.title}
              <Arrow />
            </h3>
            <div className="morph-content">
              {tab.image && <img src={tab.image} alt={tab.title} />}
              <p>{tab.content}</p>
            </div>
          </div>
        );
      })}
    </div>
  );
}
