import React from "react";
import "./UserJourneyMap.css";

export default function UserJourneyMap({ stages, rows }) {
  return (
    <div className="journey-map">
      {/* è¡¨é ­ */}
      <div className="header empty"></div>
      {stages.map((stage) => (
        <div key={stage} className="header">
          {stage}
        </div>
      ))}

      {/* å„è¡Œ */}
      {rows.map((row) => (
        <React.Fragment key={row.label}>
          <div className="row-label">{row.label}</div>
          {row.type === "emotion" ? (
            // ðŸŽ¢ æƒ…ç·’æ›²ç·šè¡Œ
            <div className="emotion-line" style={{ gridColumn: "span 4" }}>
              <EmotionLine values={row.data} />
            </div>
          ) : (
            // å…¶ä»–ä¸€èˆ¬è¡Œ
            row.data.map((cell, i) => (
              <div key={i} className="cell">
                <div dangerouslySetInnerHTML={{ __html: cell }} />
              </div>
            ))
          )}
        </React.Fragment>
      ))}
    </div>
  );
}

/* âœ… å­å…ƒä»¶ï¼šç¹ªè£½æƒ…ç·’æŠ˜ç·šåœ– */
function EmotionLine({ values, width = 500, height = 200, padding = 20 }) {
  const step = (width - padding) / (values.length - 1);

  // å°‡ points å­—ä¸²çµ„å¥½
  const points = values
    .map((v, i) => {
      const x = i * step + padding / 2;
      const y = height - (v / 100) * height;
      return `${x},${y}`;
    })
    .join(" ");

  return (
    <svg
      viewBox={`0 0 ${width} ${height}`}
      preserveAspectRatio="xMidYMid meet" // ä¿æŒæ¯”ä¾‹
      style={{ width: "100%", height: `${height}px` }} // å¯¬åº¦è‡ªé©æ‡‰ï¼Œé«˜åº¦å›ºå®š
    >
      <polyline
        fill="none"
        stroke="#fff"
        strokeWidth="2"
        points={points}
        strokeLinecap="round"
      />
      {values.map((v, i) => (
        <circle
          key={i}
          cx={i * step + padding / 2}
          cy={height - (v / 100) * height}
          r="3"
          fill="#fff"
        />
      ))}
    </svg>
  );
}
