import React, { useRef, useEffect, useState } from "react";
import { motion, AnimatePresence, delay } from "framer-motion";
import "./UserJourneyMap.css";

export default function UserJourneyMap({ stages, rows, id }) {
  return (
    <div className="journey-map">
      {/* Ë°®È†≠ */}
      <div className="header empty"></div>
      {stages.map((stage) => (
        <div key={stage} className="header">
          {stage}
        </div>
      ))}

      {/* ÂêÑË°å */}
      {rows.map((row) => (
        <React.Fragment key={row.label}>
          <div className="row-label">{row.label}</div>
          {row.type === "emotion" ? (
            // üé¢ ÊÉÖÁ∑íÊõ≤Á∑öË°å
            <AnimatePresence mode="wait">
              <motion.div
                key={JSON.stringify(row.data)}
                className="emotion-line"
                initial={{ opacity: 0, y: 7 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: 7 }}
                transition={{ duration: 0.2 }}
                style={{ gridColumn: "span 4" }}
              >
                <EmotionLine values={row.data} colorId={id} />
              </motion.div>
            </AnimatePresence>
          ) : (
            // ÂÖ∂‰ªñ‰∏ÄËà¨Ë°å
            row.data.map((cell, i) => (
              <div key={i} className="cell">
                <FadingCell content={cell} sequence={i} />
              </div>
            ))
          )}
        </React.Fragment>
      ))}
    </div>
  );
}

/* Â≠êÂÖÉ‰ª∂ÔºöÂ∏∂Ê∑°ÂÖ•Ê∑°Âá∫ÊïàÊûúÁöÑÊñáÂ≠ó */
function FadingCell({ content, sequence }) {
  return (
    <AnimatePresence mode="wait">
      <motion.div
        key={content} // key ËÆäÂåñÊôÇËß∏ÁôºÂãïÁï´
        initial={{ opacity: 0, y: 7 }}
        animate={{ opacity: 1, y: 0 }}
        exit={{ opacity: 0, y: 7 }}
        transition={{ duration: 0.2, delay: 0.1 * sequence }}
        dangerouslySetInnerHTML={{ __html: content }}
      />
    </AnimatePresence>
  );
}

/* ‚úÖ Â≠êÂÖÉ‰ª∂ÔºöÁπ™Ë£ΩÊÉÖÁ∑íÊäòÁ∑öÂúñ */

function EmotionLine({ values = [], padding = 20, colorId }) {
  const containerRef = useRef(null);
  const [coords, setCoords] = useState([]);
  const [pathLength, setPathLength] = useState(0);

  // Ë®≠ÂÆöÈ°èËâ≤
  let color = "#fff";
  if (colorId) {
    switch (colorId) {
      case "dessert": color = "#fc868cff"; break;
      case "nightMarket": color = "#ffc75eff"; break;
      case "nightAnimal": color = "#5DC2F4"; break;
      case "chillCamping": color = "#ee8866ff"; break;
      case "mountainClimbing": color = "#f0ff9bff"; break;
      default: color = "#fff";
    }
  }

  // Ë®àÁÆóÂ∫ßÊ®ôËàá path Èï∑Â∫¶
  useEffect(() => {
    if (!containerRef.current || !values.length) return;
    const { clientWidth: width, clientHeight: height } = containerRef.current;
    const step = (width - padding) / Math.max(values.length - 1, 1);

    const newCoords = values.map((v, i) => ({
      x: i * step + padding / 2,
      y: height - (v / 100) * height,
    }));
    setCoords(newCoords);

    // Ë®àÁÆó path D ËàáÈï∑Â∫¶
    const pathD = newCoords.map((p, i) => `${i === 0 ? "M" : "L"} ${p.x} ${p.y}`).join(" ");

    // Âà©Áî®Ëá®ÊôÇ SVG Ë®àÁÆó path Èï∑Â∫¶
    const tempPath = document.createElementNS("http://www.w3.org/2000/svg", "path");
    tempPath.setAttribute("d", pathD);
    setPathLength(tempPath.getTotalLength());
  }, [values, padding]);

  if (!coords.length) return <div ref={containerRef} style={{ width: "100%", height: "100%" }} />;

  // ÁîüÊàê path D
  const pathD = coords.map((p, i) => `${i === 0 ? "M" : "L"} ${p.x} ${p.y}`).join(" ");

  return (
    <div ref={containerRef} style={{ width: "100%", height: "100%", position: "relative" }}>
      <svg style={{ width: "100%", height: "100%" }}>
        {/* Â∫ïÁ∑ö */}
        <path fill="none" stroke="rgba(255,255,255,0.2)" strokeWidth="2" d={pathD} />

        {/* ÂãïÁï´Á∑öÊ¢ù */}
        <motion.path
          d={pathD}
          fill="none"
          stroke={color}
          strokeWidth={2}
          strokeLinecap="round"
          strokeDasharray={pathLength}
          strokeDashoffset={pathLength}
          initial={{ strokeDashoffset: pathLength }}
          animate={{ strokeDashoffset: 0 }}
          transition={{ duration: 0.7, ease: "easeInOut" }}
        />

        {/* ÂúìÈªû‰æùÂ∫èÂá∫Áèæ */}
        {coords.map((p, i) => (
          <motion.circle
            key={i}
            cx={p.x}
            cy={p.y}
            r={5}
            fill="#fff"
            initial={{ opacity: 0, scale: 0 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{
              delay: (i / (coords.length - 1)) * 0.7, // Ë∑üÁ∑öÊ¢ùÂãïÁï´ÂêåÊ≠•
              duration: 0.2,
            }}
          />
        ))}
      </svg>
    </div>
  );
}
