import React, { forwardRef, useState } from "react";
import MorphCardTabs from "../components/MorphCardTabs";
import { motion, AnimatePresence, easeInOut, alpha, rgba } from "framer-motion";

const tabs = [
  {
    id: "lobby",
    title: "大廳",
    content:
      "提供一個平台，網羅地標的社團，根據分類和關鍵字快速找到自己想找的地標",
    image: "product_lobby.mov",
  },
  {
    id: "createClub",
    title: "創建社團",
    content:
      "可選主題模式來根據這季主題來競賽，或可選一般模式不限主題地自由創作",
    image: "product_createClub.mov",
  },
  {
    id: "exploreMap",
    title: "探索地圖",
    content: "顯示不同分類的地標點，可點擊該類標籤呈現於地圖和列表",
    image: "product_exploreMap.mov",
  },
  {
    id: "label",
    title: "標籤",
    content: "以不同顏色和圖示來客製不同標題的標籤，讓視覺紹上一目瞭然",
    image: "product_label.mov",
  },
  {
    id: "addPin",
    title: "新增標點",
    content:
      "透過關鍵字搜尋或是地圖釘選來快速新增地標，而地標資訊也之後可再編輯",
    image: "product_addPin.mov",
  },
  {
    id: "infoCard",
    title: "資訊卡",
    content: "以圖鑑資訊卡的樣式，網羅大家踩該點分享的照片和心得",
    image: "product_infoCard.mov",
  },
  {
    id: "post",
    title: "貼文串",
    content: "即時瀏覽最近有什麼新貼文或新地標，快速看見大家的活動和分享",
    image: "product_post.mov",
  },
  {
    id: "ranking",
    title: "排行榜",
    content: "透過排行模式參閱最新或最熱門的地點是什麼",
    image: "product_ranking.mov",
  },
];

const Works = forwardRef(({ id }, ref) => {
  const [active, setActive] = useState(null);
  const activeTab = tabs.find((tab) => tab.id === active);

  return (
    <div id={id} ref={ref}>
      <div className="worksContainer">
        <div className="container">
          <div className="tabContainer">
            <div className="title">
              <span>產品與特色</span>
              <p>Ｍotivation Concept</p>
            </div>
            <MorphCardTabs data={tabs} active={active} setActive={setActive} />
          </div>
          <div className="contentContainer">
            <AnimatePresence mode="wait">
              {activeTab && (
                <div className="frame">
                  <motion.div
                    className="frameMask"
                    key={activeTab.id}
                    initial={{ background: rgba(0, 0, 0, 1) }}
                    animate={{ background: rgba(0, 0, 0, 0) }}
                    exit={{ background: rgba(0, 0, 0, 1) }}
                    transition={{ duration: 0.5, ease: "easeInOut" }}
                  >
                    <video
                      src={`/asset/${activeTab.image}`}
                      autoPlay
                      loop
                      muted
                      playsInline
                      className="video"
                    ></video>
                  </motion.div>
                  <img
                    className="frame_overlay"
                    src="/asset/frame.png"
                    alt=""
                  />
                </div>
              )}
            </AnimatePresence>
            {/* {active ? (
              <div className="frame">
                <div className="frameMask">
                  <video
                    src={`/asset/${activeTab.image}`}
                    autoPlay
                    loop
                    muted
                    playsInline
                    className="video"
                  ></video>
                </div>
                <img className="frame_overlay" src="/asset/frame.png" alt="" />
              </div>
            ) : null} */}
          </div>
        </div>
      </div>
    </div>
  );
});

export default Works;
