import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { ReactComponent as Arrow } from "../images/arrow.svg";
import "./MorphCardTabs.css";

export default function MorphCardTabs({ data }) {
  const [active, setActive] = useState(null);

  // 點擊空白處關閉展開卡片
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (!e.target.closest(".morph-card")) {
        setActive(null);
      }
    };
    window.addEventListener("click", handleClickOutside);
    return () => window.removeEventListener("click", handleClickOutside);
  }, []);

  return (
    <div className="morph-container">
      {data.map((tab) => {
        const isActive = active === tab.id;

        return (
          <motion.div
            key={tab.id}
            className="morph-card"
            onClick={(e) => {
              e.stopPropagation();
              setActive(isActive ? null : tab.id);
            }}
            animate={{
              width: isActive ? 320 : 140, // 展開/收回寬度
            }}
            transition={{
              type: "spring",
              stiffness: 25,
              damping: 28,
              mass: 0.7,
            }}
          >
            <h3>
              {tab.title}
              <Arrow />
            </h3>

            <AnimatePresence>
              {isActive && (
                <motion.div
                  key="content"
                  className="morph-content"
                  initial={{ opacity: 0, height: 0 }}
                  animate={{ opacity: 1, height: "auto" }}
                  exit={{ opacity: 0, height: 0 }}
                  transition={{ duration: 0.3, ease: "easeInOut" }}
                >
                  {tab.image && <img src={tab.image} alt={tab.title} />}
                  <p>{tab.content}</p>
                </motion.div>
              )}
            </AnimatePresence>
          </motion.div>
        );
      })}
    </div>
  );
}
