function EmotionLine({ values, padding = 20 }) {
  const containerRef = useRef(null);
  const pathRef = useRef(null);
  const [size, setSize] = useState({ width: 500, height: 200 });
  const [pathLength, setPathLength] = useState(null); // ‚Üê ÊîπÁÇ∫ null Ë°®Á§∫„ÄåÂ∞öÊú™Ê∫ñÂÇôÂ•Ω„Äç

  // üîπ Áõ£Ê∏¨ÂÆπÂô®Â∞∫ÂØ∏ËÆäÂåñ
  useEffect(() => {
    function updateSize() {
      if (containerRef.current) {
        const { clientWidth, clientHeight } = containerRef.current;
        setSize({ width: clientWidth, height: clientHeight });
      }
    }
    updateSize();
    window.addEventListener("resize", updateSize);
    return () => window.removeEventListener("resize", updateSize);
  }, []);

  const { width, height } = size;
  const step = (width - padding) / (values.length - 1);

  const points = values
    .map((v, i) => {
      const x = i * step + padding / 2;
      const y = height - (v / 100) * height;
      return `${x},${y}`;
    })
    .join(" ");

  // üîπ ÈáèÊ∏¨ path Èï∑Â∫¶
  useEffect(() => {
    if (pathRef.current) {
      const length = pathRef.current.getTotalLength();
      setPathLength(length);
    }
  }, [points]);

  // ‚õî Â¶ÇÊûúÈÇÑÊ≤íÂèñÂæó path Èï∑Â∫¶ÔºåÂ∞±ÂÖà‰∏ç render ÂãïÁï´
  if (pathLength === null) {
    return <div ref={containerRef} style={{ width: "100%", height: "100%" }} />;
  }

  return (
    <div ref={containerRef} style={{ width: "100%", height: "100%" }}>
      <svg
        viewBox={`0 0 ${width} ${height}`}
        preserveAspectRatio="xMidYMid meet"
        style={{ width: "100%", height: "100%" }}
      >
        {/* üé¨ Á∑öÊ¢ùÂæûÂ∑¶ÂæÄÂè≥Âª∂‰º∏ÂãïÁï´ */}
        <motion.polyline
          ref={pathRef}
          fill="none"
          stroke="#fff"
          strokeWidth="2"
          strokeLinecap="round"
          points={points}
          initial={{
            strokeDasharray: pathLength,
            strokeDashoffset: pathLength,
          }}
          animate={{
            strokeDashoffset: 0,
          }}
          transition={{
            duration: 1.8,
            ease: "easeInOut",
          }}
        />

        {/* ÁØÄÈªû‰æùÂ∫èÊ∑°ÂÖ• */}
        {values.map((v, i) => (
          <motion.circle
            key={i}
            cx={i * step + padding / 2}
            cy={height - (v / 100) * height}
            r="3"
            fill="#fff"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{
              duration: 0.3,
              delay: 0.3 + i * 0.08, // Á∑öÊ¢ùÂá∫ÁèæÂæåÂÜç‰æùÂ∫è‰∫Æ
            }}
          />
        ))}
      </svg>
    </div>
  );
}
