import React, { useEffect, useState } from "react";
import MenuIcon from "./components/MenuIcon";
import classNames from "classnames";

function Header({ sectionRefs }) {
  const [activeSection, setActiveSection] = useState("");
  const [isOpen, setIsOpen] = useState(false);

  // 監測各區塊進入視窗
  useEffect(() => {
    const options = { root: null, threshold: 0.5 };
    const observer = new IntersectionObserver((entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) setActiveSection(entry.target.id);
      });
    }, options);

    Object.values(sectionRefs).forEach((ref) => {
      if (ref.current) observer.observe(ref.current);
    });

    return () => observer.disconnect();
  }, [sectionRefs]);

  // 滑動到特定區塊
  function movePage(page) {
    const target = document.getElementById(page);
    target?.scrollIntoView({ behavior: "smooth" });

    // 手機版點選後關閉選單
    if (window.innerWidth < 768) setIsOpen(false);
  }

  // 切換手機版選單
  const toggleMenu = () => setIsOpen((prev) => !prev);

  // 手機版點擊外部自動關閉
  useEffect(() => {
    if (window.innerWidth >= 768) return; // 電腦版不需要

    const handleClickOutside = (event) => {
      const menu = document.getElementById("menu-list");
      const button = document.querySelector(".MenuButton_Icon");

      if (menu && button && !menu.contains(event.target) && !button.contains(event.target)) {
        setIsOpen(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [isOpen]);

  return (
    <div id="header">
      <div className="logo">
        Portfolio <br />
        <span>Benjamin.</span>
      </div>

      {/* 手機版選單按鈕 */}
      <div className="menu_mobile">
        <button
          type="button"
          className="MenuButton_Icon"
          onClick={toggleMenu}
          aria-expanded={isOpen}
          aria-controls="menu-list"
        >
          <MenuIcon isOpen={isOpen} />
        </button>
      </div>

      {/* 選單 */}
      <div
        id="menu-list"
        className={classNames("menu", { open: isOpen || window.innerWidth >= 768 })}
      >
        <div className="menuContainer">
          {["home", "about", "skill", "experience", "works", "contact"].map((page) => (
            <button
              key={page}
              className={classNames("MenuButton", { active: activeSection === page })}
              onClick={() => movePage(page)}
            >
              {page === "home" && "首頁"}
              {page === "about" && "介紹"}
              {page === "skill" && "技能"}
              {page === "experience" && "經歷"}
              {page === "works" && "作品"}
              {page === "contact" && "聯絡"}
            </button>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Header;
