import React, { useRef, useEffect, useState } from "react";
import { motion, AnimatePresence, delay } from "framer-motion";
import "./UserJourneyMap.css";

export default function UserJourneyMap({ stages, rows, id }) {
  return (
    <div className="journey-map">
      {/* Ë°®È†≠ */}
      <div className="header empty"></div>
      {stages.map((stage) => (
        <div key={stage} className="header">
          {stage}
        </div>
      ))}

      {/* ÂêÑË°å */}
      {rows.map((row) => (
        <React.Fragment key={row.label}>
          <div className="row-label">{row.label}</div>
          {row.type === "emotion" ? (
            // üé¢ ÊÉÖÁ∑íÊõ≤Á∑öË°å
            <AnimatePresence mode="wait">
              <motion.div
                key={JSON.stringify(row.data)}
                className="emotion-line"
                initial={{ opacity: 0, y: 7 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: 7 }}
                transition={{ duration: 0.2 }}
                style={{ gridColumn: "span 4" }}
              >
                <EmotionLine values={row.data} colorId={id} />
              </motion.div>
            </AnimatePresence>
          ) : (
            // ÂÖ∂‰ªñ‰∏ÄËà¨Ë°å
            row.data.map((cell, i) => (
              <div key={i} className="cell">
                <FadingCell content={cell} sequence={i} />
              </div>
            ))
          )}
        </React.Fragment>
      ))}
    </div>
  );
}

/* Â≠êÂÖÉ‰ª∂ÔºöÂ∏∂Ê∑°ÂÖ•Ê∑°Âá∫ÊïàÊûúÁöÑÊñáÂ≠ó */
function FadingCell({ content, sequence }) {
  return (
    <AnimatePresence mode="wait">
      <motion.div
        key={content} // key ËÆäÂåñÊôÇËß∏ÁôºÂãïÁï´
        initial={{ opacity: 0, y: 7 }}
        animate={{ opacity: 1, y: 0 }}
        exit={{ opacity: 0, y: 7 }}
        transition={{ duration: 0.2, delay: 0.1 * sequence }}
        dangerouslySetInnerHTML={{ __html: content }}
      />
    </AnimatePresence>
  );
}

/* ‚úÖ Â≠êÂÖÉ‰ª∂ÔºöÁπ™Ë£ΩÊÉÖÁ∑íÊäòÁ∑öÂúñ */
function EmotionLine({ values = [], padding = 20, colorId }) {
  const containerRef = useRef(null);
  const pathRef = useRef(null);
  const circleRefs = useRef([]);

  const duration = 700; // Á∑öÊ¢ùÂãïÁï´Á∏ΩÊôÇÈï∑ 2s

  let color = "#fff";

  if (colorId) {
    switch (colorId) {
      case "dessert": {
        color = "#fc868cff";
        break;
      }
      case "nightMarket": {
        color = "#ffc75eff";
        break;
      }
      case "nightAnimal": {
        color = "#5DC2F4";
        break;
      }
      case "chillCamping": {
        color = "#ee8866ff";
        break;
      }
      case "mountainClimbing": {
        color = "#f0ff9bff";
        break;
      }
      default: {
        color = "#fff";
        break;
      }
    }
  }

  useEffect(() => {
    if (!values.length) return;

    const { clientWidth: width, clientHeight: height } = containerRef.current;
    const step = (width - padding) / Math.max(values.length - 1, 1);

    // Ë®àÁÆó path d
    const coords = values.map((v, i) => ({
      x: i * step + padding / 2,
      y: height - (v / 100) * height,
    }));

    const pathD = coords
      .map((p, i) => `${i === 0 ? "M" : "L"} ${p.x} ${p.y}`)
      .join(" ");

    pathRef.current.setAttribute("d", pathD);

    // Ë®àÁÆóÁ∑öÈï∑
    const len = pathRef.current.getTotalLength();
    if (pathRef.current.style) {
      pathRef.current.style.strokeDasharray = len;
      pathRef.current.style.strokeDashoffset = len;
    }

    // ÂàùÂßãÂåñÂúìÈªû‰ΩçÁΩÆ
    coords.forEach((p, i) => {
      const c = circleRefs.current[i];
      c.setAttribute("cx", p.x);
      c.setAttribute("cy", p.y);
    });

    let start = null;
    const animate = (timestamp) => {
      if (!start) start = timestamp;
      const elapsed = timestamp - start;
      const t = Math.min(elapsed / duration, 1);

      // Êõ¥Êñ∞Á∑öÊ¢ù
      pathRef.current.style.strokeDashoffset = len * (1 - t);

      // Êõ¥Êñ∞ÂúìÈªû
      coords.forEach((_, i) => {
        const pointProgress = i / (values.length - 1);
        if (t >= pointProgress) {
          circleRefs.current[i].style.opacity = 1;
        }
      });

      if (t < 1) requestAnimationFrame(animate);
    };

    requestAnimationFrame(animate);
  }, [values, padding]);

  return (
    <div
      ref={containerRef}
      style={{ width: "100%", height: "100%", position: "relative" }}
    >
      <svg style={{ width: "100%", height: "100%" }}>
        {/* ÁÅ∞Â∫ïÁ∑ö */}
        <path fill="none" stroke="rgba(255,255,255,0.2)" strokeWidth="2" />

        {/* ËóçÁ∑ö */}
        <path
          ref={pathRef}
          fill="none"
          stroke={color}
          strokeWidth="2"
          strokeLinecap="round"
        />

        {/* ÂúìÈªû */}
        {values.map((_, i) => (
          <circle
            key={i}
            ref={(el) => (circleRefs.current[i] = el)}
            r="5"
            fill="#fff"
            style={{ opacity: 0 }}
          />
        ))}
      </svg>
    </div>
  );
}
